import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

import axios from 'axios';

const LearnerDashboard = () => {
  const [assignments, setAssignments] = useState({ needsWork: [], complete: [], inReview: [] });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAssignments = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) throw new Error('Authentication token not found');

        const decoded = jwtDecode(token); // Use jwtDecode here
        const userId = decoded.sub; // Make sure 'sub' matches the claim used in your JWT for user ID

        const response = await axios.post('http://localhost:8080/assignments', { userId }, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const fetchedAssignments = response.data;
        categorizeAssignments(fetchedAssignments);
      } catch (error) {
        console.error('Failed to fetch assignments', error);
        navigate('/login'); // Redirect to login if there's an error (e.g., token is missing or invalid)
      }
    };

    fetchAssignments();
  }, [navigate]);

  const categorizeAssignments = (fetchedAssignments) => {
    const needsWork = fetchedAssignments.filter(a => a.status === null || a.status === 'not_completed');
    const complete = fetchedAssignments.filter(a => a.status === 'complete');
    const inReview = fetchedAssignments.filter(a => a.status === 'in_review');

    setAssignments({ needsWork, complete, inReview });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login'); // Redirect to login after logout
  };

  const handleNavigateToSubmitAssignment = () => {
    navigate('/submit-assignment'); // Route to the assignment submission form
  };

  // Add any styles here that you want to apply to your components
  const sectionStyle = { /* ... */ };
  const buttonStyle = { /* ... */ };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>LEARNER DASHBOARD</h1>
      <button onClick={handleNavigateToSubmitAssignment} style={buttonStyle}>
        Submit New Assignment
      </button>
      <button onClick={handleLogout} style={buttonStyle}>Logout</button>
      <div style={sectionStyle}>
        <h3>Needs Work</h3>
        {assignments.needsWork.map((assignment, index) => (
          <div key={index}>{assignment.title}</div> // Ensure 'title' exists in your assignments
        ))}
      </div>
      <div style={sectionStyle}>
        <h3>Complete</h3>
        {assignments.complete.map((assignment, index) => (
          <div key={index}>{assignment.title}</div> // Ensure 'title' exists in your assignments
        ))}
      </div>
      <div style={sectionStyle}>
        <h3>In Review</h3>
        {assignments.inReview.map((assignment, index) => (
          <div key={index}>{assignment.title}</div> // Ensure 'title' exists in your assignments
        ))}
      </div>
    </div>
  );
};

export default LearnerDashboard;
