import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode'; // Ensure this is the correct import based on your jwt-decode package version
import axios from 'axios';

const LearnerDashboard = () => {
  const [assignments, setAssignments] = useState({
    needsWork: [],
    complete: [],
    inReview: [],
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAssignments = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('Authentication token not found');
        }

        const decoded = jwtDecode(token);
        const username = decoded.sub;

        const response = await axios.post(
          'http://localhost:8080/assignments',
          { username },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        const fetchedAssignments = response.data;
        categorizeAssignments(fetchedAssignments);
      } catch (error) {
        console.error('Failed to fetch assignments', error);
        navigate('/login');
      }
    };

    fetchAssignments();
  }, [navigate]);

  const categorizeAssignments = (fetchedAssignments) => {
    // Categorization logic
  };

  const renderAssignment = (assignment) => (
    <div key={assignment.id}>
      <p>ID: {assignment.id}</p>
      <p>Status: {assignment.status || 'Not Started'}</p>
      <p>Number: {assignment.number}</p>
      <p>GitHub URL: <a href={assignment.githubUrl}>{assignment.githubUrl}</a></p>
      <p>Branch: {assignment.branch}</p>
      <p>Review Video URL: <a href={assignment.reviewVideoUrl}>{assignment.reviewVideoUrl}</a></p>
      <p>User ID: {assignment.userId}</p>
      <p>Code Reviewer ID: {assignment.codeReviewerId}</p>
    </div>
  );

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>LEARNER DASHBOARD</h1>
      <button onClick={() => navigate('/submit-assignment')}>Submit New Assignment</button>
      <button onClick={() => {
        localStorage.removeItem('token');
        navigate('/login');
      }}>Logout</button>

      <section>
        <h3>Needs Work</h3>
        {assignments.needsWork.map(renderAssignment)}
      </section>

      <section>
        <h3>Complete</h3>
        {assignments.complete.map(renderAssignment)}
      </section>

      <section>
        <h3>In Review</h3>
        {assignments.inReview.map(renderAssignment)}
      </section>
    </div>
  );
};

export default LearnerDashboard;
