import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode';
import axios from 'axios';

const LearnerDashboard = () => {
  const [assignments, setAssignments] = useState({ needsWork: [], complete: [], inReview: [] });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAssignments = async () => {
      try {
        const token = localStorage.getItem('token');
        const decoded = jwt_decode(token);
        const userId = decoded.sub; // Replace 'sub' with the actual claim that contains the user ID

        const response = await axios.post('http://localhost:8080/assignments', {
          userId: userId
        }, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const fetchedAssignments = response.data;
        categorizeAssignments(fetchedAssignments);
      } catch (error) {
        console.error('Failed to fetch assignments', error);
        // Handle error, e.g. redirect to login if token is invalid
      }
    };

    fetchAssignments();
  }, []);

  const categorizeAssignments = (fetchedAssignments) => {
    const needsWork = fetchedAssignments.filter(a => a.status === null || a.status === 'not_completed');
    const complete = fetchedAssignments.filter(a => a.status === 'complete');
    const inReview = fetchedAssignments.filter(a => a.status === 'in_review');

    setAssignments({ needsWork, complete, inReview });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login'); // Redirect to login after logout
  };

  const handleNavigateToSubmitAssignment = () => {
    navigate('/submit-assignment'); // Route to the assignment submission form
  };

  // ... rest of your component with styles

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>LEARNER DASHBOARD</h1>
      <button onClick={handleNavigateToSubmitAssignment}>Submit New Assignment</button>
      <button onClick={handleLogout}>Logout</button>
      <div>
        <h3>Needs Work</h3>
        {/* Render assignments that need work */}
        {assignments.needsWork.map((assignment, index) => (
          <div key={index}>{assignment.title}</div>
        ))}
      </div>
      <div>
        <h3>Complete</h3>
        {/* Render completed assignments */}
        {assignments.complete.map((assignment, index) => (
          <div key={index}>{assignment.title}</div>
        ))}
      </div>
      <div>
        <h3>In Review</h3>
        {/* Render assignments in review */}
        {assignments.inReview.map((assignment, index) => (
          <div key={index}>{assignment.title}</div>
        ))}
      </div>
    </div>
  );
};

export default LearnerDashboard;
