import React from 'react';
import { useNavigate } from 'react-router-dom';

const LearnerDashboard = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Perform logout logic here
    // For example, clear tokens, set user state to logged out, etc.
  };

  const handleNavigateToSubmitAssignment = () => {
    navigate('/submit-assignment'); // Route to the assignment submission form
  };

  // Styles for sections and buttons
  const sectionStyle = {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'flex-start',
    marginBottom: '20px', // Space between sections
    overflowX: 'auto', // Makes content scrollable horizontally
  };

  const buttonStyle = {
    padding: '10px 20px',
    margin: '5px',
    cursor: 'pointer',
  };

  const headerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    margin: '10px 0',
  };

  return (
    <div>
      <div style={headerStyle}>
        <button onClick={handleNavigateToSubmitAssignment} style={buttonStyle}>
          Submit New Assignment
        </button>
        <h2>Welcome Learner</h2>
        <button onClick={handleLogout} style={buttonStyle}>Logout</button>
      </div>
      <div style={sectionStyle}>
        <h3>Needs Work</h3>
        {/* List items that need work */}
      </div>
      <div style={sectionStyle}>
        <h3>Complete</h3>
        {/* List completed items */}
      </div>
      <div style={sectionStyle}>
        <h3>In Review</h3>
        {/* List items under review */}
      </div>
    </div>
  );
};

export default LearnerDashboard;
