import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {jwtDecode} from 'jwt-decode'; // Correct import based on your jwt-decode package version
import axios from 'axios';

const LearnerDashboard = () => {
  const [assignments, setAssignments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAssignments = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('Authentication token not found');
        }

        const decoded = jwtDecode(token);
        const username = decoded.sub;

        const response = await axios.post(
          'http://localhost:8080/assignments',
          { username },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        setAssignments(response.data);
      } catch (error) {
        console.error('Failed to fetch assignments', error);
        navigate('/login');
      }
    };

    fetchAssignments();
  }, [navigate]);

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>LEARNER DASHBOARD</h1>
      {assignments.map((assignment) => (
        <div key={assignment.id}>
          <p>ID: {assignment.id}</p>
          <p>Status: {assignment.status || 'Not Started'}</p>
          <p>Number: {assignment.number}</p>
          <p>GitHub URL: <a href={assignment.githubUrl}>{assignment.githubUrl}</a></p>
          <p>Branch: {assignment.branch}</p>
          <p>Review Video URL: <a href={assignment.reviewVideoUrl}>{assignment.reviewVideoUrl}</a></p>
          <p>User ID: {assignment.userId}</p>
          <p>Code Reviewer ID: {assignment.codeReviewerId}</p>
        </div>
      ))}
    </div>
  );
};

export default LearnerDashboard;
