import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';
import axios from 'axios';

const LearnerDashboard = () => {
  const [assignments, setAssignments] = useState({
    needsWork: [],
    complete: [],
    inReview: [],
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAssignments = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('Authentication token not found');
        }

        const decoded = jwtDecode(token);
        const username = decoded.sub; // Extract username from the token

        const response = await axios.post(
          'http://localhost:8080/assignments',
          { username },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        const fetchedAssignments = response.data;
        categorizeAssignments(fetchedAssignments);
      } catch (error) {
        console.error('Failed to fetch assignments', error);
        navigate('/login'); // Redirect to login if there's an error
      }
    };

    fetchAssignments();
  }, [navigate]);

  const categorizeAssignments = (fetchedAssignments) => {
    const needsWork = fetchedAssignments.filter(
      (a) => a.status === null || a.status === 'not_completed'
    );
    const complete = fetchedAssignments.filter((a) => a.status === 'complete');
    const inReview = fetchedAssignments.filter((a) => a.status === 'in_review');

    setAssignments({ needsWork, complete, inReview });
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>LEARNER DASHBOARD</h1>
      <button onClick={() => navigate('/submit-assignment')}>Submit New Assignment</button>
      <button onClick={() => {
        localStorage.removeItem('token');
        navigate('/login');
      }}>Logout</button>

      <div>
        <h3>Needs Work</h3>
        {assignments.needsWork.map((assignment, index) => (
          <div key={index}>
            <p>ID: {assignment.id}</p>
            <p>Status: {assignment.status || 'Not Started'}</p>
            <p>Number: {assignment.number}</p>
            <p>GitHub URL: {assignment.githubUrl}</p>
            {/* Add other properties as needed */}
          </div>
        ))}
      </div>

      {/* Repeat for 'complete' and 'inReview' */}
      {/* ... */}
    </div>
  );
};

export default LearnerDashboard;
