import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ReviewEditForm = () => {
  const [reviewVideoUrl, setReviewVideoUrl] = useState('');
  const { id } = useParams(); // Assuming the ID is part of the URL
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    axios.get(`http://localhost:8080/assignment/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      // Assuming the response data is the assignment object
      setReviewVideoUrl(response.data.reviewVideoUrl);
    })
    .catch(error => {
      console.error('Failed to fetch review details', error);
    });
  }, [id, navigate]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const token = localStorage.getItem('token');

    axios.put(`http://localhost:8080/assignments/${id}`, { reviewVideoUrl }, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(() => {
      navigate('/reviewer-dashboard'); // Redirect to the reviewer dashboard
    })
    .catch(error => {
      console.error('Failed to update review', error);
    });
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Review Edit View</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Review Video URL</label>
          <input
            type="text"
            value={reviewVideoUrl || ''}
            onChange={(e) => setReviewVideoUrl(e.target.value)}
          />
        </div>
        <button type="submit">Update Review</button>
        <button type="button" onClick={() => navigate('/reviewer-dashboard')}>Back to Dashboard</button>
      </form>
    </div>
  );
};

export default ReviewEditForm;
