import React from 'react';
import { useNavigate } from 'react-router-dom';

const LearnerDashboard = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Perform logout logic here
    // For example, clear tokens, set user state to logged out, etc.
    navigate('/login'); // Redirect to login after logout
  };

  const handleNavigateToSubmitAssignment = () => {
    navigate('/submit-assignment'); // Route to the assignment submission form
  };

  // Styles for sections and buttons
  const sectionContainerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  };

  const sectionStyle = {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    width: '100%',
    maxWidth: '800px', // Adjust the max width as needed
    margin: '10px 0',
  };

  const buttonContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    margin: '20px 0',
  };

  const buttonStyle = {
    padding: '10px 20px',
    margin: '0 10px',
    cursor: 'pointer',
  };

  const titleStyle = {
    textAlign: 'center',
    margin: '20px 0',
  };

  return (
    <div style={sectionContainerStyle}>
      <h1 style={titleStyle}>LEARNER DASHBOARD</h1>
      <div style={buttonContainerStyle}>
        <button onClick={handleNavigateToSubmitAssignment} style={buttonStyle}>
          Submit New Assignment
        </button>
        <button onClick={handleLogout} style={buttonStyle}>Logout</button>
      </div>
      <div style={sectionStyle}>
        <h3>Needs Work</h3>
        {/* List items that need work */}
      </div>
      <div style={sectionStyle}>
        <h3>Complete</h3>
        {/* List completed items */}
      </div>
      <div style={sectionStyle}>
        <h3>In Review</h3>
        {/* List items under review */}
      </div>
    </div>
  );
};

export default LearnerDashboard;
